import React, { useState, useEffect, useRef, useCallback } from 'react';
import GameDisplay from './components/GameDisplay';
import StatsPanel from './components/StatsPanel';
import UpgradePanel from './components/UpgradePanel';
import { GameState, Monster, MonsterType, UpgradeType, Player, DamageText, Particle } from './types/gameTypes';
import { getRandomAttackPhrase } from './data/attackPhrases';
import './App.css';

const App: React.FC = () => {
  const [gameState, setGameState] = useState<GameState>({
    player: {
      x: 100,
      y: 300,
      width: 60,
      height: 80,
      hp: 100,
      maxHp: 100,
      level: 1,
      exp: 0,
      expMax: 100,
      attack: 10,
      gold: 0,
      animFrame: 0,
      animSpeed: 0.1,
      isAttacking: false,
      attackCooldown: 0
    },
    monsters: [],
    currentFloor: 1,
    autoAttack: false,
    particles: [],
    damageTexts: []
  });

  const autoAttackRef = useRef<NodeJS.Timeout | null>(null);
  const [currentAttackPhrase, setCurrentAttackPhrase] = useState<string>('');

  const spawnMonster = useCallback(() => {
    const types: MonsterType[] = [
      { name: 'スライム', color: '#00ff00', hp: 20 },
      { name: 'ゴブリン', color: '#ffaa00', hp: 35 },
      { name: 'オーク', color: '#ff4400', hp: 50 },
      { name: 'ドラゴン', color: '#aa0000', hp: 100 }
    ];

    const floorMultiplier = 1 + (gameState.currentFloor - 1) * 0.5;
    const typeIndex = Math.min(Math.floor(gameState.currentFloor / 3), types.length - 1);
    const type = types[typeIndex];

    const monster: Monster = {
      x: 600,
      y: 250,
      width: 80,
      height: 100,
      hp: Math.floor(type.hp * floorMultiplier),
      maxHp: Math.floor(type.hp * floorMultiplier),
      name: type.name,
      color: type.color,
      animFrame: 0,
      animSpeed: 0.05
    };

    setGameState(prevState => ({
      ...prevState,
      monsters: [...prevState.monsters, monster]
    }));
  }, [gameState.currentFloor]);

  useEffect(() => {
    spawnMonster();
  }, []);

  useEffect(() => {
    if (gameState.autoAttack && !autoAttackRef.current) {
      autoAttackRef.current = setInterval(() => {
        setGameState(prevState => {
          if (!prevState.player.isAttacking && 
              prevState.player.attackCooldown <= 0 && 
              prevState.monsters.length > 0) {
            return handleAttack(prevState);
          }
          return prevState;
        });
      }, 1000);
    } else if (!gameState.autoAttack && autoAttackRef.current) {
      clearInterval(autoAttackRef.current);
      autoAttackRef.current = null;
    }

    return () => {
      if (autoAttackRef.current) {
        clearInterval(autoAttackRef.current);
      }
    };
  }, [gameState.autoAttack]);

  const handleAttack = useCallback((currentState: GameState = gameState): GameState => {
    if (currentState.monsters.length === 0) return currentState;

    // Generate random attack phrase
    setCurrentAttackPhrase(getRandomAttackPhrase());

    const monster = currentState.monsters[0];
    const damage = currentState.player.attack + Math.floor(Math.random() * 5);
    const updatedMonster: Monster = { ...monster, hp: monster.hp - damage };

    const newDamageTexts: DamageText[] = [...currentState.damageTexts, {
      id: Date.now(),
      x: monster.x + monster.width / 2,
      y: monster.y,
      text: damage.toString(),
      color: '#ff6b6b',
      life: 60,
      maxLife: 60
    }];

    const newParticles: Particle[] = [...currentState.particles];
    for (let i = 0; i < 8; i++) {
      newParticles.push({
        id: Date.now() + i,
        x: monster.x + monster.width / 2,
        y: monster.y + monster.height / 2,
        vx: (Math.random() - 0.5) * 4,
        vy: (Math.random() - 0.5) * 4,
        color: '#ff6b6b',
        life: 20,
        maxLife: 20
      });
    }

    let newState: GameState = {
      ...currentState,
      player: {
        ...currentState.player,
        isAttacking: true,
        attackCooldown: 30
      },
      monsters: [updatedMonster, ...currentState.monsters.slice(1)],
      damageTexts: newDamageTexts,
      particles: newParticles
    };

    if (updatedMonster.hp <= 0) {
      newState = killMonster(newState, updatedMonster);
    }

    setTimeout(() => {
      setGameState(prevState => ({
        ...prevState,
        player: {
          ...prevState.player,
          isAttacking: false
        }
      }));
    }, 200);

    return newState;
  }, [gameState]);

  const killMonster = useCallback((currentState: GameState, monster: Monster): GameState => {
    const expGain = Math.floor(monster.maxHp / 2);
    const goldGain = Math.floor(monster.maxHp / 5) + currentState.currentFloor;

    let newPlayer: Player = {
      ...currentState.player,
      exp: currentState.player.exp + expGain,
      gold: currentState.player.gold + goldGain
    };

    const newDamageTexts: DamageText[] = [
      ...currentState.damageTexts,
      {
        id: Date.now() + 1000,
        x: monster.x + monster.width / 2,
        y: monster.y - 20,
        text: `+${expGain} EXP`,
        color: '#00ff00',
        life: 60,
        maxLife: 60
      },
      {
        id: Date.now() + 2000,
        x: monster.x + monster.width / 2,
        y: monster.y - 40,
        text: `+${goldGain} G`,
        color: '#ffff00',
        life: 60,
        maxLife: 60
      }
    ];

    if (newPlayer.exp >= newPlayer.expMax) {
      newPlayer = levelUp(newPlayer);
      newDamageTexts.push({
        id: Date.now() + 3000,
        x: newPlayer.x + newPlayer.width / 2,
        y: newPlayer.y,
        text: 'LEVEL UP!',
        color: '#ff00ff',
        life: 60,
        maxLife: 60
      });
    }

    setTimeout(() => {
      spawnMonster();
    }, 500);

    return {
      ...currentState,
      player: newPlayer,
      monsters: currentState.monsters.filter(m => m !== monster),
      damageTexts: newDamageTexts
    };
  }, [spawnMonster]);

  const levelUp = useCallback((player: Player): Player => {
    return {
      ...player,
      level: player.level + 1,
      exp: player.exp - player.expMax,
      expMax: Math.floor(player.expMax * 1.5),
      attack: player.attack + 3,
      maxHp: player.maxHp + 10,
      hp: player.maxHp + 10
    };
  }, []);

  const handleCanvasClick = useCallback(() => {
    if (!gameState.player.isAttacking && gameState.player.attackCooldown <= 0) {
      setGameState(handleAttack);
    }
  }, [gameState.player.isAttacking, gameState.player.attackCooldown, handleAttack]);

  const handleUpgrade = useCallback((type: UpgradeType) => {
    setGameState(prevState => {
      const newState: GameState = { ...prevState };

      switch (type) {
        case 'attack':
          if (newState.player.gold >= 10) {
            newState.player = {
              ...newState.player,
              gold: newState.player.gold - 10,
              attack: newState.player.attack + 5
            };
          }
          break;
        case 'autoAttack':
          if (newState.player.gold >= 50 && !newState.autoAttack) {
            newState.player = {
              ...newState.player,
              gold: newState.player.gold - 50
            };
            newState.autoAttack = true;
          }
          break;
        case 'hp':
          if (newState.player.gold >= 25) {
            newState.player = {
              ...newState.player,
              gold: newState.player.gold - 25,
              maxHp: newState.player.maxHp + 20,
              hp: newState.player.maxHp + 20
            };
          }
          break;
        default:
          break;
      }

      return newState;
    });
  }, []);

  const nextFloor = useCallback(() => {
    setGameState(prevState => ({
      ...prevState,
      currentFloor: prevState.currentFloor + 1,
      monsters: []
    }));
    setTimeout(() => {
      spawnMonster();
    }, 100);
  }, [spawnMonster]);

  return (
    <div className="app">
      <div className="game-container">
        <GameDisplay 
          gameState={gameState}
          onAttackClick={handleCanvasClick}
          attackPhrase={currentAttackPhrase}
        />

        <div className="ui-panel">
          <StatsPanel player={gameState.player} floor={gameState.currentFloor} />
          <UpgradePanel 
            player={gameState.player}
            autoAttack={gameState.autoAttack}
            onUpgrade={handleUpgrade}
            onNextFloor={nextFloor}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
